const Homescreen = () => {
  const [upcommingMovies, setupcommingMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await axios.get(
        `https://api.themoviedb.org/3/movie/popular?api_key=${api_key}&language=en-US&page=1`
      );
      setupcommingMovies(data.results);
      console.log(data.results);
    };
    fetchData();
  }, []);

  return (
    <div>
      <Nav />
      <Banner />

      <Row title={upcommingMovies} arr={upcommingMovies} />
      {/* <Row title={" Popular onNetflix"} arr={upcommingMovies} />
      <Row title={"Mylist"} arr={upcommingMovies} />
      <Row title={"upcomming movies"} arr={upcommingMovies} /> */}
    </div>
  );
};

export default Homescreen;
