import React from 'react';
const imgURL = "https://image.tmdb.org/t/p/original";

const Card = ({ img }) => {
  return <img className="card" src={img} alt="cover" />;
};

const Row = ({title, arr = []}) => {
  return (

    
      <div className="row">
        <h2>{title}</h2>
        <div>
          {arr.map((index, item) => (
            <Card key={index} img={`${imgURL}/${item.poster_path}`} />
          ))}
        </div>
      </div>
      );
  
 
}

export default Row;
