import React, { useEffect, useState } from "react";
import Nav from "../Nav/Nav";
import Banner from "../Banner/Banner";
import Card from "../Card/Card";
import axios from "axios";

const api_key = "51560beb187e60946bf18e17a32f687c";
const url = "https://api.themoviedb.org/3";
const imgURL = "https://image.tmdb.org/t/p/original";

const Homescreen = () => {
  const [upcommingMovies, setupcommingMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await axios.get(
        "https://api.themoviedb.org/3/movie/popular?api_key=51560beb187e60946bf18e17a32f687c&language=en-US&page=1"
      );
      setupcommingMovies(data.results);
      console.log(data);
    };
    fetchData();
  }, []);

  return (
    <div>
      <Nav />
      <Banner />

      <Card img={`${imgURL}/${data.results.poster_path}`} />
    </div>
  );
};

export default Homescreen;
