import React, { useEffect, useState } from "react";
import Nav from "../Nav/Nav";
import "../Homescreen/Homescreen.css";
import Banner from "../Banner/Banner";
import axios from "axios";

const api_key = "51560beb187e60946bf18e17a32f687c";
const url = "https://api.themoviedb.org/3";
const imgURL = "https://image.tmdb.org/t/p/original";



const Row = ({ title, arr }) => {
  const Card = ({ img }) => <img className="card" src={img} alt="cover" />;
  <div className="row">
    <h2>{title}</h2>
    <div>
      {arr.map((item, index) => {
        <Card key={index} img={`${imgURL}/${item.poster_path}`} />;
      })}
    </div>
  </div>;
};

const Homescreen = () => {
  const [upcommingMovies, setupcommingMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await axios.get(
        `https://api.themoviedb.org/3/movie/popular?api_key=${api_key}&language=en-US&page=1`
      );
      setupcommingMovies(data.results);
      console.log(data.results);
    };
    fetchData();
  }, []);

  return (
    <div>
      <Nav />
      <Banner />
      <Row title={"upcomming movies"} arr={upcommingMovies} />
      <Row title={" Popular onNetflix"} arr={upcommingMovies} />
      <Row title={"Mylist"} arr={upcommingMovies} />
      <Row title={"upcomming movies"} arr={upcommingMovies} />
    </div>
  );
};

export default Homescreen;
